mapper.select:
select  from T_ACTIVITY_COUPON
where DEL_FLAG = 0
 and ACTIVITY_ID = #{activityId}
 and COUPON_ID = #{couponId}

    and ACTIVITY_ID in

        #{item}

SELECT tci.ID as id, tci.COUPON_NAME as couponName, tci.COUPON_CODE as couponCode, tci.COUPON_TYPE as couponType, tci.COUPON_PHOTO as couponPhoto FROM T_ACTIVITY_COUPON tac LEFT JOIN T_COUPON_INFO tci ON tac.COUPON_ID = tci.ID AND tci.DEL_FLAG = 0 WHERE tac.DEL_FLAG = 0
AND tac.ACTIVITY_ID = #{activityId}
AND tci.BUSINESS_TYPE = #{businessType}
AND tci.COUPON_TYPE = #{couponType}
AND tci.COUPON_NAME = #{couponName}
AND tci.COUPON_CODE = #{couponCode}

select COUPON_ID as couponId, SUM(GRANT_NUM) as grantNum from T_GRANT_INFO
and ACTIVITY_ID = #{activityId}
and COUPON_ID = #{couponId}
group by COUPON_ID

mapper.update:
update T_ACTIVITY_COUPON set INVENTORY_NUM = #{inventoryNum}, UPDATE_BY = #{updateBy},
UPDATE_TIME = #{updateTime}
where ACTIVITY_ID = #{activityId} and COUPON_ID = #{couponId} and DEL_FLAG = '0'
