mapper.select:
SELECT tci.ID, tci.ACTIVITY_NAME, tci.START_TIME, tci.END_TIME, tci.APPROVE_STATUS, tci.STATUS FROM T_ACTIVITY_INFO tci inner JOIN T_ACTIVITY_COUPON tac ON tci.ID = tac.ACTIVITY_ID AND tac.DEL_FLAG = 0 WHERE tci.DEL_FLAG = 0
AND tac.COUPON_ID = #{couponId}
AND tci.ACTIVITY_NAME = #{activityName}
AND tci.APPROVE_STATUS = #{approveStatus}
AND tci.STATUS = #{status}

select count(*) from T_ACTIVITY_INFO where DEL_FLAG = 0

     and ((to_char(START_TIME,'yyyy-mm-dd hh24:mi:ss') >= #{beginTime}
     and to_char(START_TIME,'yyyy-mm-dd hh24:mi:ss') <= #{endTime})
     or (to_char(START_TIME,'yyyy-mm-dd hh24:mi:ss') <= #{beginTime}
     and to_char(END_TIME,'yyyy-mm-dd hh24:mi:ss') >= #{endTime})
     or (to_char(END_TIME,'yyyy-mm-dd hh24:mi:ss') >= #{beginTime}
     and to_char(END_TIME,'yyyy-mm-dd hh24:mi:ss') <= #{endTime}))

select  from T_ACTIVITY_INFO
where DEL_FLAG = 0 and STATUS in

    #{item}

