mapper.select:
select  from T_COUPON_INFO

     and BUSINESS_TYPE = #{businessType}
     and COUPON_TYPE = #{couponType}
     and COUPON_ADD like concat(concat('%', #{couponAdd}), '%')
     and COUPON_NAME like concat(concat('%', #{couponName}), '%')
     and COUPON_CODE like concat(concat('%', #{couponCode}), '%')
     and USE_DESC = #{useDesc}
     and VER_CHANNEL = #{verChannel}
     and PAY_TYPE = #{payType}
     and DEL_FLAG = #{delFlag}
    and DEL_FLAG = 0

order by CREATE_TIME desc

mapper.update:
update T_COUPON_INFO
BUSINESS_TYPE = #{businessType}, COUPON_TYPE = #{couponType}, COUPON_ADD = #{couponAdd}, COUPON_NAME = #{couponName}, COUPON_CODE = #{couponCode}, COUPON_PHOTO = #{couponPhoto}, USE_DESC = #{useDesc}, VER_CHANNEL = #{verChannel}, PAY_TYPE = #{payType}, UPDATE_BY = #{updateBy}, DEL_FLAG = #{delFlag}, SEND_TYPE = #{sendType}, CONDITION_DESC = #{conditionDesc}, PERSON_LIMIT = #{personLimit}, DATE_LIMIT = #{dateLimit}, MACHINE_LIMIT = #{machineLimit}, UPDATE_TIME = SYSDATE
where ID = #{id}

mapper.delete:
update T_COUPON_INFO set DEL_FLAG = 1, UPDATE_TIME = SYSDATE, UPDATE_BY = #{updateUser}
where ID in

    #{id}

mapper.select:
SELECT b.id, b.couponName, b.couponCode, b.couponPhoto, b.couponType, b.contactNum, b.createTime FROM ( SELECT a.ID AS id, a.COUPON_NAME AS couponName, a.COUPON_CODE AS couponCode, a.COUPON_PHOTO AS couponPhoto, a.COUPON_TYPE AS couponType, COUNT( a.ACTIVITY_ID ) AS contactNum, a.CREATE_TIME AS createTime FROM ( SELECT tci.ID, tci.COUPON_NAME, tci.COUPON_CODE, to_char( tci.COUPON_PHOTO ) AS COUPON_PHOTO, tci.COUPON_TYPE, tai.ID AS ACTIVITY_ID, tci.CREATE_TIME FROM T_COUPON_INFO tci LEFT JOIN T_ACTIVITY_COUPON tac ON tci.ID = tac.COUPON_ID AND tac.DEL_FLAG = 0 LEFT JOIN T_ACTIVITY_INFO tai ON tai.ID = tac.ACTIVITY_ID AND tai.DEL_FLAG = 0 WHERE tci.DEL_FLAG = 0 and tci.BUSINESS_TYPE = #{businessType} and tci.COUPON_TYPE = #{couponType} and tci.COUPON_NAME like concat(concat('%', #{couponName}), '%') and tci.COUPON_CODE like concat(concat('%', #{couponCode}), '%')
and to_char(tci.CREATE_TIME,'yyyy-mm-dd') >= #{params.beginTime}
and to_char(tci.CREATE_TIME,'yyyy-mm-dd') <= #{params.endTime}
) a GROUP BY a.ID, a.COUPON_NAME, a.COUPON_CODE, a.COUPON_PHOTO, a.COUPON_TYPE, a.CREATE_TIME ) b ORDER BY b.createTime DESC

select from T_COUPON_INFO where DEL_FLAG = 0
and ID in
#{item}

SELECT COUPON_ID AS couponId, count( * ) AS receiveNum FROM T_USER_COUPON WHERE COUPON_ID in
#{item}
GROUP BY COUPON_ID

select ID, USER_NO, COUPON_ID, STATUS, PHONENUMBER, APP_ACCOUNT, CARD_NUMBER,
RECEIVE_TIME, DEVICE_NO, IP_ADDRESS, MAC_ADDRESS, ACTIVITY_ID, WRITE_OFF_CODE,
LINK_URL, SEND_TYPE
from T_USER_COUPON


        and ACTIVITY_ID = #{activityId}


        and COUPON_ID = #{couponId}

select count(*) from T_USER_COUPON where 1=1

    and to_char(RECEIVE_TIME,'yyyy-mm-dd hh24:mi:ss') >= #{beginTime}
    and to_char(RECEIVE_TIME,'yyyy-mm-dd hh24:mi:ss') <= #{endTime}

select count(*) from T_USER_COUPON where STATUS = '2'

    and to_char(RECEIVE_TIME,'yyyy-mm-dd hh24:mi:ss') >= #{beginTime}
    and to_char(RECEIVE_TIME,'yyyy-mm-dd hh24:mi:ss') <= #{endTime}

SELECT
    count( * )
FROM
    T_USER_COUPON


        and COUPON_ID = #{couponId}


        and PHONENUMBER = #{phoneNumber}


        and DEVICE_NO = #{deviceNo}


        and to_char(RECEIVE_TIME,'yyyy-mm-dd') = #{receiveTime}

SELECT tci.ID as couponId, tai.ID as activityId, tci.COUPON_NAME as couponName, tci.COUPON_TYPE as couponType, tai.ACTIVITY_NAME as activityName, tuc.RECEIVE_TIME as receiveTime, tuc.STATUS as status, tuc.USE_TIME as useTime, tac.RECEIVE_TYPE AS receiveType, tac.EFFECT_TYPE AS effectType, tac.EFFECT_START_TIME AS effectStartTime, tac.EFFECT_END_TIME AS effectEndTime, tac.TAKE_DATE_NUM AS takeDateNum, tac.EFFECT_DATE_NUM AS effectDateNum, tac.SAME_ACTIVITY_FLAG AS sameActivityFlag, tac.DIFF_ACTIVITY_FLAG AS diffActivityFlag, tac.OUT_ACTIVITY_FLAG AS outActivityFlag, tct.RESTRICTION_TYPE, tct.DIS_AMOUNT, tct.FULL_FIX_SUB_CONDITION, tct.DIS_FIX_AMOUNT, tct.FULL_SUB_CONDITION, tct.FULL_SUB_AMOUNT, tct.FULL_RESTRICTION, tct.SALE_NUM, tct.UPPER_LIMIT_FLAG, tct.UPPER_LIMIT_AMOUNT, tct.MONEY, tct.ORDER_AMOUNT, tct.PAY_AMOUNT FROM T_USER_COUPON tuc LEFT JOIN T_COUPON_INFO tci ON tuc.COUPON_ID = tci.ID AND tci.DEL_FLAG = '0' LEFT JOIN T_ACTIVITY_INFO tai ON tai.ID = tuc.ACTIVITY_ID AND tai.DEL_FLAG = '0' LEFT JOIN T_ACTIVITY_COUPON tac ON tac.COUPON_ID = tuc.COUPON_ID AND tac.ACTIVITY_ID = tai.ID AND tac.DEL_FLAG = '0' LEFT JOIN T_COUPON_THRESHOLD tct ON tct.COUPON_ID = tuc.COUPON_ID AND tct.DEL_FLAG = '0'
and tuc.COUPON_ID = #{couponId}
and tuc.USER_NO = #{userNo}
and tuc.PHONENUMBER = #{phoneNumber}
and tuc.APP_ACCOUNT = #{appAccount}
and tuc.CARD_NUMBER = #{cardNumber}
ORDER BY tuc.RECEIVE_TIME desc

select ID, USER_NO, COUPON_ID, STATUS, PHONENUMBER, APP_ACCOUNT, CARD_NUMBER,
RECEIVE_TIME, DEVICE_NO, IP_ADDRESS, MAC_ADDRESS, ACTIVITY_ID, WRITE_OFF_CODE,
LINK_URL, SEND_TYPE, USE_TIME
from T_USER_COUPON


        and ACTIVITY_ID = #{activityId}


        and COUPON_ID = #{couponId}


        and PHONENUMBER = #{phoneNumber}


        and USER_NO = #{userNo}


        and CARD_NUMBER = #{cardNumber}

mapper.update:
update T_USER_COUPON set STATUS = #{status}, USE_TIME = #{useTime}


        and ID = #{id}


        and ACTIVITY_ID = #{activityId}


        and COUPON_ID = #{couponId}


        and PHONENUMBER = #{phoneNumber}


        and USER_NO = #{userNo}


        and CARD_NUMBER = #{cardNumber}
