mapper.select:
select  from T_COUPON_THRESHOLD

     and COUPON_ID = #{couponId}
     and COUPON_TYPE = #{couponType}
     and RESTRICTION_TYPE = #{restrictionType}
     and FULL_SUB_CONDITION = #{fullSubCondition}
     and FULL_SUB_AMOUNT = #{fullSubAmount}
     and DIS_AMOUNT = #{disAmount}
     and FULL_RESTRICTION = #{fullRestriction}
     and SALE_NUM = #{saleNum}
     and UPPER_LIMIT_FLAG = #{upperLimitFlag}
     and UPPER_LIMIT_AMOUNT = #{upperLimitAmount}
     and money = #{money}
     and ORDER_AMOUNT = #{orderAmount}
     and PAY_AMOUNT = #{payAmount}

select  from T_COUPON_THRESHOLD
where DEL_FLAG = 0

    and COUPON_ID = #{couponId,jdbcType=VARCHAR}


    and COUPON_ID in

        #{item,jdbcType=VARCHAR}

mapper.update:
update T_COUPON_THRESHOLD
COUPON_ID = #{couponId}, COUPON_TYPE = #{couponType}, RESTRICTION_TYPE = #{restrictionType}, FULL_SUB_CONDITION = #{fullSubCondition}, FULL_SUB_AMOUNT = #{fullSubAmount}, DIS_AMOUNT = #{disAmount}, FULL_RESTRICTION = #{fullRestriction}, SALE_NUM = #{saleNum}, UPPER_LIMIT_FLAG = #{upperLimitFlag}, UPPER_LIMIT_AMOUNT = #{upperLimitAmount}, money = #{money}, ORDER_AMOUNT = #{orderAmount}, PAY_AMOUNT = #{payAmount}, CREATE_BY = #{createBy}, CREATE_TIME = #{createTime}, UPDATE_BY = #{updateBy}, UPDATE_TIME = #{updateTime}, DEL_FLAG = #{delFlag}, FULL_FIX_SUB_CONDITION = #{fullFixSubCondition}, DIS_FIX_AMOUNT = #{disFixAmount},
where DEL_FLAG = 0
and id = #{id}
and COUPON_ID = #{couponId}

